0.program:
import("stdfaust.lib");
lp_cut = hslider("lp_cut",{lp_cut},50,1000,1);
hp_cut = hslider("hp_cut",{hp_cut},1,120,1);
process = no.noise:fi.lowpass(3,lp_cut):fi.highpass(10,hp_cut);

1.program:
import("stdfaust.lib");
carrier = hslider("carrier",{carrier},20,1000,1);
amp = hslider("amp",{amp},1,20,1);
sineOsc(f) = +(f/ma.SR) ~ ma.frac:*(2*ma.PI) : sin;
sawOsc(f) = +(f/ma.SR) ~ ma.frac;
process = sineOsc(amp)*sawOsc(carrier);

2.program:
import("stdfaust.lib");
amp = hslider("amp",{amp},0,5,0.01);
carrier = hslider("carrier",{carrier},0,4,0.01);
sineOsc(f) = +(f/ma.SR) ~ ma.frac:*(2*ma.PI) : sin;
process = no.noise*sineOsc(carrier)*amp;

3.program:
import("stdfaust.lib");
saw_freq = hslider("saw_freq",{saw_freq},20,1000,1);
sine_freq = hslider("sine_freq",{sine_freq},20,1000,1);
sineOsc(f) = +(f/ma.SR) ~ ma.frac:*(2*ma.PI) : sin;
sawOsc(f) = +(f/ma.SR) ~ ma.frac;
process = sineOsc(sine_freq)+sawOsc(saw_freq);

4.program:
import("stdfaust.lib");
carrier = hslider("carrier",{carrier},20,300,0.1);
amp = hslider("amp",{amp},0,10,0.1);
sineOsc(f) = +(f/ma.SR) ~ ma.frac:*(2*ma.PI) : sin;
process = sineOsc(amp)*sineOsc(carrier);
